import { createSlice } from "@reduxjs/toolkit";
import { legacy_createStore as createStore } from "redux";
const intialState={counter: 0,toggle:true}

const counterSlice=createSlice({
  name:"counter",
  intialState,
  reducers:{
    increment(state){
      state:state.counter+1
    },
    decrement(state){
      state:state.counter-1
    },
    increase(state,amount){
      state:state.counter+action.amount
    }
  }

})

const reducerfn = (state = intialState, action) => {
  if (action.type == "increment")
    return {
      counter: state.counter + 1,
      toggle:state.toggle
    };
  if (action.type == "decrement") {
    return {
      counter: state.counter - 1,
      toggle:state.toggle
    };
  }
  if (action.type == "increase")
  return {
    counter: state.counter + action.amount,
    toggle:state.toggle
  };
  // if (action.type == "decrementbyFive") {
  //   return {
  //     counter: state.counter - 5,
  //     toggle:state.toggle
  //   };
  // }
  if (action.type == "toggle") {
    return {
      counter: state.counter,
      toggle:!state.toggle
    };
  }
  return {
    counter: state.counter,
    toggle:false
  };
};

const store = createStore(reducerfn); //will hold the reduce function

console.log(store.getState());
export default store;
